package com.assinment.loops2;

public class PrintEvenDoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("print even no 121 to 229 :");
		int n=121;
		do {
			if(n%2==0)
				System.out.println(n+"hi");
			n++;
		} while (n<=229);
	}

}
