package com.assinment.loops2;

public class PrintOddNoWhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n=521;
			System.out.println("print odd no 521 to 229 :");
			while (n>=229) {
				if(n%2!=0)
					System.out.println(n);
				n--;
			}
	}

}
