package com.assinment.loops2;

public class UseBreakFor {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Use Break In for loop"+"\n print 1 to 50:");
		for (int i = 1; i < 100; i++) {
			System.out.println(i);
			if (i==50) 
				break;
			
		}
	}

}
